  }


  return (
    <div className="App">
      <Header/>
      <NavBar />
      <Switch>
        <Route exact path ="/">
        <SearchBar setSearch={setSearch} search={search}/>
          <HomePage 
          article = {newDisplayedArticleArray} 
          articleDetail = {articleDetail}
          setArticleDetail = {setArticleDetail}/>
        </Route>
        <Route path='/article'>
          <Article updatedArticleDetail = {updatedArticleDetail}/>
        </Route>
        <Route path="/new_article">
          <NewArticleForm/>
        </Route>
      </Switch>
      <Footer/>
    </div>
  );
}

export default App;
